setInterval(function(){
    if(
        typeof macroButton != "undefined" || typeof farmBoss != "undefined"
        || $('#clickable').length > 0
    ){
        client.emit("botDetected",false);
    }
}, 10000);

$(document).click(function(event){
    if(!event.hasOwnProperty("originalEvent"))
        client.emit("botDetected",false);
});