    var tab = document.getElementsByName("tabs")[0];
    var running = false;
    var macroButton = document.createElement('div');
     
    macroButton.innerHTML+= '<input onclick="change()" id="clickable" type="button" value="Farm Bosses (off)" id=myButton1"></input>';
    tab.appendChild(macroButton);
     
    var runMacroButton = document.getElementById("clickable");
    var timeVar;
    var clickVar;
    function change(){
            if(running === false){
                    runMacroButton.value = "Farm Bosses (on)";
                    running = true;
                    timerVar  = setInterval (function() {bossFarm (); }, 30000); //you can change this to however many seconds you want
                    clickVar = setInterval(function(){clickContinue();}, 5000);
            }
            else{
                    runMacroButton.value = "Farm Bosses (off)";
                    running = false;
                    clearInterval(timeVar);
                    clearInterval(clickVar);
            }
    }
     
    function clickContinue(){
            var continuebutton = document.getElementsByName("continue")[0];
            if(document.getElementById("popup").style.display != ""){
            continuebutton.click();
    }
    }
     
    function bossFarm() {
            var d = new Date().getTime();
        var cash = 0;
        var numKnights = 0;
        var knightPrice = 300;
        var numSkilled = 0;
        var skilledPrice = 1000;
        var numTemplars = 0;
        var templarPrice = 5000;
            var requiredCash = 0;
        var client = io.connect('http://212.1.209.149:466');
           
                    var numKnightsWanted = 400000000;  //Put whatever the fuck you want in here
                    var numSkilledWanted = 100000000;
                    var numTemplareWanted = 10000000;
     
        cash = game.money;
        numKnights = document.getElementsByName("knight")[0].getElementsByTagName("*")[15].innerHTML;
        numSkilled = document.getElementsByName("advknight")[0].getElementsByTagName("*")[15].innerHTML;
        numTemplars = document.getElementsByName("templar")[0].getElementsByTagName("*")[15].innerHTML;
     
        numKnights = numKnights.replace(",", "");
        numKnights = numKnights.replace(",", "");
        var knightdifference = numKnightsWanted - parseInt(numKnights);
     
        numSkilled = numSkilled.replace(",", "");
        numSkilled = numSkilled.replace(",", "");
        var skilleddifference = numSkilledWanted - parseInt(numSkilled);
     
        numTemplars = numTemplars.replace(",", "");
        numTemplars = numTemplars.replace(",", "");
        var templardifference = numTemplareWanted - parseInt(numTemplars);
     
        if (parseInt(numKnights) < numKnightsWanted) {
            requiredCash = parseInt(parseInt(numKnights) * parseInt(knightPrice));
            if (requiredCash < cash) client.emit('buySoldier', {
                soldierType: "knight",
                amount: knightdifference
            });
        }
        if (parseInt(numSkilled) < numSkilledWanted) {
            requiredCash = parseInt(parseInt(numSkilled) * skilledPrice);
            if (requiredCash < cash) client.emit('buySoldier', {
                soldierType: 'advknight',
                amount: skilleddifference
            });
        }
        if (parseInt(numTemplars) < numTemplareWanted) {
            requiredCash = parseInt(parseInt(numTemplars) * templarPrice);
            if (requiredCash < cash) client.emit('buySoldier', {
                soldierType: 'templar',
                amount: templardifference
            });
        }
        if (!game.bossScenario['active'] && (d - game.bossLastGenerated) > game.bossCooldown) {
            client.emit('summonBoss', true);
        }
                if(game.bossCurrency > 0){
                if(game.scientistBCMode === false){
                        document.getElementsByName("scientistBuyMode")[0].click;
                        var amount = parseInt(parseInt(game.bossCurrency)/parseInt(game.scientistCostBC));
                        client.emit('buyXScientists', amount);
                }
                else{
                        var amount = parseInt(parseInt(game.bossCurrency)/parseInt(game.scientistCostBC));
                        client.emit('buyXScientists', amount);
                }
       }
    }