function Control(clients){
    this.clients = clients;
    this.initUpdate = false;
    this.updateComing = false;
    this.kick = [];
}

Control.prototype.updatePlayerCount = function(players){
    this.clients.emit('playerCount', players);
}

Control.prototype.startUpdate = function(){
    var self = this;
    if(this.initUpdate){
        var seconds = 60;
        this.countdown = setInterval(function(){
            self.clients.emit('update', seconds);
            if(seconds == 0){
                console.log('[UPDATE] Stopping server ...');
                clearInterval(this.countdown);
                process.abort();
            }
            
            seconds--;
        },1000);
    }
}

//export class
exports.Control = Control;